var myApp=angular.module("myApp",["ngRoute","TheControllers"]);myApp.config(["$routeProvider",function(e){e.when("/search",{templateUrl:"search",controller:"SearchController"}).when("/about",{templateUrl:"about",controller:"AboutController"}).when("/create",{templateUrl:"create",controller:"RecipeController"}).when("/details/:name",{templateUrl:"details/:name",controller:"DetailsController"}).when("/tmpIngredient",{templateUrl:"tmpIngredient",controller:"IngredientController"}).otherwise({redirectTo:"/search"})}]);